def js_string(python_string):
    return string.replace(python_string, "'", "\\'")

def js_job_started(cell_id, job_id):
    jsString = """
require([
  'base/js/namespace',
  'nbextensions/methodCell/main'
], function (Jupyter, KBaseMethodsExtension) {
    KBaseMethodsExtension.send({
        type: 'runstatus',
        cellId: '${cellid}', 
        status: 'job_started',
        job_id: '${job_id}'
    });
});
"""
