#
# Simulates a call to send a method request.
# A method request returns a job id (and other stuff?)
#
def exec_method(method_fun, params):
    result = method_fun(params)
    return {
        'job_id': result['job_id']
    }


def run_method(args):
    params = args[u"params"]
    method = args[u"method"]
    cell_id = args[u"cell"][u"id"]
    method_fun = find_method(method)
    if (method_fun == None):
        return js_error(cell_id, 'Sorry, cannot find method ' + method[u'name'])

    # in real life this method call will return a job id and perhaps other run stats
    try:
        # nb method s are actually asynchronous
        ##return js_status(cell_id, 'running')
        result = exec_method(method_fun, params)
        return js_job_started(cell_id, result['job_id'])
    except Exception as e:
        # error = sys.exc_info()[0]
        error = str(e)
        print e
        return js_error(cell_id, error)

