transform_service_url = "https://ci.kbase.us/services/transform/"
ujs_service_url = "https://ci.kbase.us/services/userandjobstate/"
awe_service_url = "https://ci.kbase.us/services/awe-api/"

token = os.environ["KB_AUTH_TOKEN"]

transform_client = biokbase.Transform.Client.Transform(url=transform_service_url, token=token)
ujs_client = biokbase.userandjobstate.client.UserAndJobState(url=ujs_service_url, token=token)

# ported from https://github.com/kbase/transform/blob/master/lib/biokbase/Transform/drivers.py
def get_job_debug(awe_job_id=None, ujs_job_id=None):
    debug_details = {}
    debug_details["ujs"] = {}

    # can't pull this info because the ujs_job_id info for UJS is
    # gone by the time we get here
    #try:
    #    debug_details["ujs"]["error"] = ujs_client.get_detailed_error(ujs_job_id)
    #    debug_details["ujs"]["results"] = ujs_client.get_results(ujs_job_id)
    #except Exception, e:
    #    print "Exception grabbing debug info from UJS service!"
    #    print e

    header = {}
    header["Authorization"] = "Oauth {0}".format(token)

    debug_details["awe"] = dict()

    try:
        # check awe job output
        awe_details = requests.get("{0}/job/{1}".format(awe_service_url,awe_job_id), headers=header, verify=True)

        awe_response = awe_details.json()

        debug_details["awe"]["details"] = awe_response["data"]

        awe_stdout = requests.get("{0}/work/{1}?report=stdout".format(awe_service_url,
                                  debug_details["awe"]["details"]["tasks"][0]["taskid"]+"_0"), 
                                  headers=header, verify=True).json()["data"]
        awe_stderr = requests.get("{0}/work/{1}?report=stderr".format(awe_service_url,
                                  debug_details["awe"]["details"]["tasks"][0]["taskid"]+"_0"), 
                                  headers=header, verify=True).json()["data"]

        debug_details["awe"]["stdout"] = awe_stdout
        debug_details["awe"]["stderr"] = awe_stderr
    except Exception, e:
        print "Exception contacting AWE service!"
        print e

    return debug_details

# ported from https://github.com/kbase/transform/blob/master/lib/biokbase/Transform/drivers.py
def show_job_debug(awe_job_id=None, ujs_job_id=None):
        debug_details = get_job_debug(awe_job_id, ujs_job_id)

        if not debug_details.has_key("ujs"):
            raise Exception("Missing UJS debug output")

        if debug_details["ujs"].has_key("error"):
            print "{0}".format(debug_details["ujs"]["error"])
        else:
            print "No UJS error message present"

        if debug_details["ujs"].has_key("results"):
            print "{0}".format(debug_details["ujs"]["results"])
        else:
            print "No UJS results present"

        header = dict()
        header["Authorization"] = "Oauth {0}".format(token)

        if not debug_details.has_key("awe") or not debug_details["awe"].has_key("details"):
            raise Exception("Missing AWE debug output")

        print "Additional AWE job details for debugging"
        print simplejson.dumps(debug_details["awe"]["details"], sort_keys=True, indent=4)

        if debug_details["awe"].has_key("stdout") and debug_details["awe"]["stdout"] is not None:
            stdout_lines = debug_details["awe"]["stdout"].split("\n")
            print "STDOUT : "
            for x in stdout_lines:
                print "\t" + x

        if debug_details["awe"].has_key("stderr") and debug_details["awe"]["stderr"] is not None:
            stderr_lines = debug_details["awe"]["stderr"].split("\n")
            print "STDERR : "
            for x in stderr_lines:
                print "\t" + x

# ported from https://github.com/kbase/transform/blob/master/lib/biokbase/Transform/drivers.py
def monitor_job(awe_job_id=None, 
                ujs_job_id=None, 
                success_message="Job completed!",
                error_message="Job failed!"):
    completed = ["complete", "success"]
    error = ["error", "fail"]

    # wait for UJS to complete    
    last_status = ""
    start = datetime.datetime.utcnow()
    while 1:        
        try:
            status = ujs_client.get_job_status(ujs_job_id)
        except Exception, e:
            print "Issue connecting to UJS!"
            raise

        if last_status != status[2]:
            print "{0} status update: {1}".format(status[0], status[2])
            last_status = status[2]

        if status[1] in completed:
            print "{0}".format(success_message)
            return (True, status)
        elif status[1] in error:
            print "{0}".format(error_message)
            return (False, status)

