@register_line_magic
def kb(line):
    """
    KBase magic commands. Available commands:
    get_data
    gets data from the Data Panel with the given object name
    Usage:\\
      %kb get_data object_name

    list_methods
    Lists available method ids and names. These ids can be used to look up method specs and information.
    Usage:\\
      %kb list_methods  (gets released method ids/names)
      %kb list_methods release|beta|dev

    get_method
    Returns the method spec for a single method.
    Usage:\\
      %kb get_method method_id (assumes its released)
      %kb get_method method_id release|beta|dev

    method_info
    Shows method info from a method with the given id and branch (release, beta, or dev)
    Usage:\\
      %kb method_info method_id  (assumes its released)
      %kb method_info method_id release|beta|dev
    """
    cmd = line.split()
    if cmd[0] == "get_data":
        return kb_get_data(cmd[1])
    elif cmd[0] == "list_data":
        if len(cmd) > 1:
            return kb_list_data(type=cmd[1])
        else:
            return kb_list_data()
    elif cmd[0] == "list_methods":
        tag = "release"
        if len(cmd) > 1:
            tag = cmd[1]
        return kb_list_methods(tag)
    elif cmd[0] == "get_method":
        if len(cmd) < 2:
            return "Usage: %kb get_method method_id (release|beta|dev)"
        tag = "release"
        if len(cmd) > 2:
            tag = cmd[2]
        return kb_get_method(cmd[1], tag)
    elif cmd[0] == "method_info":
        if len(cmd) < 2:
            return "Usage: %kb method_info method_id (release|beta|dev)"
        tag = "release"
        if len(cmd) > 2:
            tag = cmd[2]
        return kb_method_info(cmd[1], tag)
    else:
        print("Unknown command %s" % cmd[0])

