def kb_output_widget(widget_name, tag="release", **kwargs):
    """
    renders a widget!
    """

    if not check_tag(tag):
        raise Exception("Unknown tag %s - tag should be one of 'release', 'beta', 'dev'" % tag)

    if widget_name not in all_widgets[tag]:
        raise Exception("Widget %s not found with %s tag!" % (widget_name, tag))

    # from the widget and tag, get its signature, and set up vars
    widget_info = all_widgets[tag][widget_name]

    input_data = kwargs
